    line += " --tfcoop zeros " # disable CO-op interactions
    line += " --sexual_ratio 0.8"
    line += " --maxgd 1"
    line += " --stop_early 0.8" # stop when the mean pop. f reaches 0.8
    line += " --maxgenerations 1 --maxtime 1 "
