def write_urs_file(pathout):
    # Master regulators. . . .
    #Bcr1
    line = "0 1 0 \n"
    #tec1
    line += "1 1 0 \n"
    #efg1
    line += "2 1 0 \n"
    #ndt80
    line += "3 1 0 \n"
    #brg1 / gat2
    line += "4 1 0 \n"
    #rob1
    line += "5 1 0 \n"
    
    # Genes upregulated in biofilms. . .
    # orf19.6053
    line += "6 0 0 \n"
    # ORF19.3337
    line += "7 0 0 \n"
    # TPO4
    line += "8 0 0 \n"
    # orf19.4000
    line += "9 0 0 \n"
    # EHT1
    line += "10 0 0 \n"
    # HWP1
    line += "11 0 0 \n"
    # CAN2
    line += "12 0 0 \n"
    
    
    # Control genes (not really changed during biofilm state)
    # orf.19.3694
    #line += "13 0 0 \n"
    # orf19.1938
    line += "13 0 0 \n"
    # orf19.3902
    line += "14 0 0 \n"
    line += "15 0 0 AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA\n"
    line += "16 0 0 GGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGGG\n"
    
    fout = open( pathout, "w" )
    fout.write( line )
    fout.close()

def write_pwm_file(pathout):
    fout = open(pathout, "w")
    fout.write("pwm 0\n")
    fout.write( get_bcr1() )
    fout.write("pwm 1\n")
    fout.write( get_tec1() )
    fout.write("pwm 2\n")
    fout.write( get_efg1() )    
    fout.write("pwm 3\n")
    fout.write( get_ndt80() )
    fout.write("pwm 4\n")
    fout.write( get_brg1() )
    fout.write("pwm 5\n")
    fout.write( get_rob1() )
    fout.close()



run_mar_11_13()

    
    
    